package Serializable;import java.io.Serializable;/** * @program: spring-demo * @description: 待序列化 * @author: gonghuihui * @create: 2019-03-06 17:37 **/public class Employee implements Serializable {    public String name;    public String address;    public transient int SSN;    public int number;    public void mailCheck() {        System.out.println("Mailing a check to " + name + " " + address);    }}