package Serializable;import org.junit.jupiter.api.Test;import java.io.*;/** * @program: spring-demo * @description: 序列化对象实例 * @author: gonghuihui * @create: 2019-03-06 17:41 **/public class SerializeDemo {    @Test    public void serializeDemo() {        Employee e = new Employee();        e.name = "Reyan Ali";        e.address = "Phokka Kuan, Ambehta Peer";        e.SSN = 112233;        e.number = 101;        try {            FileOutputStream fileOut =                    new FileOutputStream("/tmp/employee.ser");            ObjectOutputStream out = new ObjectOutputStream(fileOut);            out.writeObject(e);            out.close();            fileOut.close();            System.out.println("Serialized data is saved in /tmp/employee.ser");        } catch (FileNotFoundException e1) {            e1.printStackTrace();        } catch (IOException e1) {            e1.printStackTrace();        }    }}