import org.junit.jupiter.api.Test;import java.util.Enumeration;import java.util.Hashtable;/** * @program: spring-demo * @description: Hashtable，淘汰玩意儿 * @author: gonghuihui * @create: 2019-03-05 16:07 **/public class TestHashtable {    @Test    public void testHashtable(){        Hashtable hs = new Hashtable();        Enumeration names;        String str;        double bal;        hs.put("Zara", new Double(3434.34));        hs.put("Mahnaz", new Double(123.22));        hs.put("Ayan", new Double(1312.23));        hs.put("Daisy", new Double(99.22));        hs.put("Qadir", new Double(-19.299));        names = hs.keys();        while (names.hasMoreElements()) {            str = (String) names.nextElement();            System.out.println(str + ": " + hs.get(str));        }        bal =((Double) hs.get("Zara")).doubleValue();        hs.put("Zara", new Double(bal + 1000));        System.out.println("Zara's new balance: " +                hs.get("Zara"));    }}