import org.junit.jupiter.api.Test;import java.util.EmptyStackException;import java.util.Stack;/** * @program: spring-demo * @description: 堆栈 * @author: gonghuihui * @create: 2019-03-05 14:41 **/public class TestStack {    @Test    public void testStack(){        Stack<Integer> st = new Stack<Integer>();        System.out.println("stack: " + st);        showpush(st,42);        showpush(st,66);        showpush(st,99);        shoppop(st);        shoppop(st);        shoppop(st);        try {            shoppop(st);        } catch (EmptyStackException e) {            System.out.println("empty stack");        }    }    static void showpush(Stack<Integer> st, int a){        st.push(new Integer(a));        System.out.println("push(" + a + ")");        System.out.println("statck: " + st);    }    static void shoppop(Stack<Integer> st) {        System.out.println("pop -> ");        Integer a = (Integer) st.pop();        System.out.println(a);        System.out.println("stack: " + st);    }}