import org.junit.jupiter.api.Test;import java.io.BufferedReader;import java.io.IOException;import java.io.InputStreamReader;import java.net.HttpURLConnection;import java.net.MalformedURLException;import java.net.URL;import java.net.URLConnection;/** * @program: spring-demo * @description: openURLConnection * @author: gonghuihui * @create: 2019-03-07 15:17 **/public class TestURLConn {    @Test    public void testURLConn() {        try {            URL url = new URL("http://www.runoob.com");            URLConnection urlConnection = url.openConnection();            HttpURLConnection connection = null;            if (urlConnection instanceof HttpURLConnection) {                connection = (HttpURLConnection) urlConnection;            } else {                System.out.println("请输入 URL 地址");                return;            }            BufferedReader in = new BufferedReader(                    new InputStreamReader(connection.getInputStream()));            String urlString = "";            String current;            while ((current = in.readLine()) != null) {                urlString += current;            }            System.out.println(urlString);        } catch (MalformedURLException e) {            e.printStackTrace();        } catch (IOException e) {            e.printStackTrace();        }    }}