package Thread;/** * @program: spring-demo * @description: * @author: gonghuihui * @create: 2019-03-08 15:30 **/public class DisplayMessage implements Runnable {    private String message;    public DisplayMessage(String message) {        this.message = message;    }    public void run() {        while(true) {            System.out.println(message);        }    }}