package Thread;import java.util.Date;/** * @program: spring-demo * @description: * @author: gonghuihui * @create: 2019-03-18 11:06 **/public class LockB implements Runnable{    @Override    public void run() {        try {            System.out.println(new Date().toString() + " LockB 开始执行");            while (true) {                synchronized (TestLock.obj2) {                    System.out.println(new Date().toString() + " LockB 锁住 obj2");                    Thread.sleep(3000);                    synchronized (TestLock.obj1) {                        System.out.println(new Date().toString() + " LockB 锁住 obj1");                        Thread.sleep(60 * 1000);                    }                }            }        } catch (InterruptedException e) {            e.printStackTrace();        }    }}